import { FaGraduationCap } from "react-icons/fa";

const education = [
  {
    degree: "B.Tech in Computer Science and Engineering",
    school: "MAKAUT University",
    date: "2022 – 2026",
  },
  {
    degree: "Higher Secondary (12th)",
    school: "Rishra Vidyapith School",
    date: "2020 – 2022",
  },
  {
    degree: "Secondary (10th)",
    school: "Rishra St.Thomas Academy",
    date: "2018 – 2020",
  },
];

export default function Education() {
  return (
    <section className="py-20 px-4 bg-gradient-to-r from-gray-50 to-gray-200 dark:from-gray-900 dark:to-gray-800 text-gray-800 dark:text-white">
      <div className="max-w-4xl mx-auto">
        <h2 className="text-4xl font-bold text-center mb-12">Education</h2>
        <div className="relative border-l-4 border-blue-500 dark:border-blue-400 pl-6">
          {education.map((edu, index) => (
            <div key={index} className="mb-10 relative">
              <span className="absolute -left-3 top-1.5 w-6 h-6 bg-blue-500 dark:bg-blue-400 rounded-full flex items-center justify-center text-white shadow-lg">
                <FaGraduationCap className="text-xs" />
              </span>
              <h3 className="text-xl font-semibold">{edu.degree}</h3>
              <p className="text-md font-medium text-blue-600 dark:text-blue-400">{edu.school}</p>
              <span className="text-sm text-gray-600 dark:text-gray-400">{edu.date}</span>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
