const projects = [
  { title: "Spotify Clone", tech: "HTML & CSS" },
  { title: "Amazon Clone", tech: "HTML, CSS & JS" },
  { title: "Netflix Clone", tech: "HTML, CSS & JS" },
  { title: "Scientific Calculator", tech: "HTML, CSS & JS" },
  { title: "Portfolio + Chatbot", tech: "React" },
];

export default function Projects() {
  return (
    <section className="py-20 px-4 bg-gray-100 dark:bg-gray-900 text-gray-800 dark:text-white">
      <div className="max-w-5xl mx-auto">
        <h2 className="text-4xl font-bold text-center mb-10">Projects</h2>
        <div className="grid md:grid-cols-2 gap-6">
          {projects.map((project, index) => (
            <div key={index} className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow hover:shadow-lg transition">
              <h3 className="text-2xl font-semibold mb-2">{project.title}</h3>
              <p className="text-sm text-gray-600 dark:text-gray-300">Tech used: {project.tech}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
