export default function About() {
  return (
    <section className="py-20 px-4 bg-gray-50 dark:bg-gray-900 text-gray-800 dark:text-white">
      <div className="max-w-3xl mx-auto text-center">
        <h2 className="text-4xl font-bold mb-6">About Me</h2>
        <p className="text-lg leading-relaxed">
          I am a passionate front-end developer with a solid understanding of HTML, CSS, and JavaScript.
          My experience includes modern frameworks like React, which I utilized in various academic projects
          to create responsive and user-friendly applications. I thrive on delivering optimal user experiences
          and am eager to further my skills in the tech industry.
        </p>
      </div>
    </section>
  );
}
